import React, { useState, useEffect } from 'react';
import './App.css';
import Post from './Post';
import { db } from './firebase';

function App() {
  const [posts, setPosts] = useState([]);

  // UseEffect -> runs a piece of code based on a specific condition

  useEffect(() => {
    // Every time the database is updated, snapshot will get the changes
    db.collection('posts').onSnapshot(snapshot => {
      setPosts(snapshot.docs.map(docs => doc.data()));
    })
  }, []);

  return (
    <div className="app">

      <div className = "app_header">
        <img
          className = "app_headerImage"
          src = "https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"
          alt = "Instagram Header Logo"
        />
      </div>

      {
        posts.map(post => (
          <Post username = {post.username} caption = {post.caption} imageUrl = {post.imageUrl}></Post>
        ))
      }

    </div>
  );
}

export default App;
